public class demo4 {
	public static int max(int x, int y){
		return x>y?x:y;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(max(10,4));
		double a = Math.sqrt(2);
		int b = Math.max(4, 5);		
	}

}
